{% extends "base.html" %}
{% block title %}{{ flow_step.page_title | default("Choose an Option", True) }}{% endblock %}

{% block page_content %}

{% from "subscribe_flow/progress_bar.html" import progress_bar %}
{{ progress_bar(flow_data, flow_step) }}

<section class="row subscribe">
	<div class="col-md-12">
		<div class="row head">
			<div class="col-sm-12 text-center">
				<h1 class="heading h-section text-center lrg-heading-grp">{{ flow_step.page_title | default("Choose an Option", True) }}</h1>
				<p>{{ flow_step.page_description | default("", True) }}</p>
			</div>
		</div>
		<div class="row content variants"> <!-- Begin variant list -->
			{#
				Loop over each of the variant value objects attached the current variant
			#}

			{% for value in flow_step.values %}

			<div class="item variant"> <!-- Begin variant -->
				<a href="{{ value | subscribe_flow_url }}">
					<div class="image-container" >
						{#
							Creates an image tag if one is uploaded to the variant value model. Otherwise it creates an image tag from the default image (images/variant_placeholder.jpg)
						#}
						{{ value | object_img_tag(default_img_path="images/variant_placeholder.jpg", css_class="") }}
					</div>
				</a>

				<div class="information">
					<!-- Variant value -->
					<div class="name-container line-limited">
						<div class="name">
							{{ value.value }}
						</div>
					</div>

					{#
						Creates a link which advances the subscribe flow to the next step, choosing the current variant value
					#}
					<a type="button" href="{{ value | subscribe_flow_url }}" class="btn btn-large btn-primary"  data-barley="variant-{{value.id}}-choose-btn" data-barley-editor="simple">choose</a>

					{% if value.description %}
					<div class="description-container line-limited">
						<div class="description">
							{#
								Renders the variant value description if one exists, otherwise uses the default string
							#}
							{{ value.description }}
						</div>
					</div>
					{% endif %}
				</div>

			</div> <!-- End variant -->

			{% endfor %}
		</div> <!-- End variant list -->
	</div>
</section>
{% endblock %}

{% extends "base.html" %}
{% block title %}Cart{% endblock %}

{% block page_css %}
	{{ 'css/anims.css' | asset_url | stylesheet_tag }}
{% endblock %}

{% macro cycle_abbr(product) -%}
	{% if product.billing.rebill_months == 1 -%}
		mo
	{% elif product.billing.rebill_weeks == 1 -%}
		wk
	{% else -%}
		cycle
	{% endif %}
{% endmacro %}

{% set cart = active_cart() %}

{% block page_content %}
<section class="row cart-content">
	<div class="pholder">
		<div class="col-lg-10 col-lg-offset-1">
			<div class="row">
				<div class="col-xs-12 pholder-header">
					<h1 class="heading">YOUR CART</h1>
				</div>
			</div>

			<div class="row cart-thead cart-table hidden-xs">
				<div class="col-sm-6 cart-td">
					<h3 class="heading">product</h3>
				</div>
				<div class="col-sm-2 cart-td">
					<h3 class="heading">price</h3>
				</div>
				<div class="col-sm-2 cart-td">
					<h3 class="heading">quantity</h3>
				</div>
				<div class="col-sm-2 cart-td">
					<h3 class="heading">total</h3>
				</div>
			</div>

			{% if cart.products | length > 0 %}
				{% for cart_product in cart.products %}

				{% set instance = cart_product.instance %}
				{% set product = instance.product %}
				<div class="row cart-table" id="cart-product-{{ cart_product.id }}">
					<div class="col-sm-6 cart-td">
						<div class="product-label">
							<figure class="thumb product-image-thumb">
								<a href="#">
									{{ product | object_img_tag(height=190, width=285, title='N/A', fgcolor='#aaaaaa', bgcolor='#654321') }}
								</a>
							</figure>
							<div class="meta product-info-meta">
								<h3 class="heading"><a href="#">{{ product.name }}</a></h3>
								{% if cart_product.subscription_type_term %}
									{% set is_gifted = cart.product_is_gifted(cart_product) %}

									{% set stt = cart_product.subscription_type_term %}
										<p>{{ stt.name|title }} Plan @ {% if is_gifted %}Gifted{% else %}{{ (cart_product.price / stt.num_cycles) | currency }}{% endif %}/{{ cycle_abbr(instance.product) }}</p>
									{% if stt.subscription_type.autorenew and not is_gifted %}
										<p>{{ stt.num_cycles }} deliver{% if stt.num_cycles==1 %}y{% else %}ies{% endif %}, auto-renewing.</p>
									{% else %}
										<p>{{ stt.num_cycles }} deliver{% if stt.num_cycles==1 %}y{% else %}ies{% endif %}, does not auto-renew.</p>
									{% endif %}
								{% endif %}
							</div>
						</div>
					</div>
					<div class="col-sm-2 cart-td">
						<div class="product-label">
							<span class="thumb visible-xs">Price</span>
							<div class="meta price">
								{% if product.single_purchasable %}
									{% set price = instance.price %}
								{% else %}
									{% set stt = cart_product.subscription_type_term %}
									{% set st = stt.subscription_type %}
									{% set price = instance.calc_price(st, stt) %}
								{% endif %}
								<p>{{ price | currency }} USD</p>
							</div>
						</div>
					</div>
					<div class="col-sm-2 cart-td">
						<div class="product-label">
							<span class="thumb visible-xs">Quantity</span>
							<div class="meta">
								{% if product.single_purchasable %}
									<select name="product-qty-{{ cart_product.id }}" class="product-qty-select form-control" data-cart-product="{{ cart_product.id}}">
										{% for i in range(10) %}
										<option value="{{ i + 1 }}" {%if i + 1 == cart_product.quantity%}selected{%endif%}>
											{{ i + 1 }}
										</option>
										{% endfor %}
									</select>
								{% else %}
									{{ cart_product.quantity }}
								{% endif %}
							</div>
						</div>
					</div>
					<div class="col-sm-2 cart-td">
						<div class="product-label">
							<span class="thumb visible-xs">Total</span>
							<div class="meta price">
								<p class="cart-product-price">{{ cart_product.price | currency }} USD</p>
							</div>
						</div>
					</div>

					<button type="button" data-cart-product="{{ cart_product.id }}" class="product-rem-btn close"><i class="icon icon-remove"></i><span class="sr-only">Close</span></button>
				</div>
				{% endfor %}

			<div class="row flat-well">
				<div class="col-sm-10 col-sm-offset-1 text-center">
					<div class="cart-subtotal">
						<h2 class="title">Subtotal</h2>
						<h1 class="price" id="cart-subtotal-price">
							{{ cart.product_total | currency }} {{ store | currency_code }}
						</h1>
						<p>Subtotal excludes Shipping, Tax & Discounts</p>
					</div>
					<a href="/checkout" class="btn btn-primary fixsize">CHECKOUT</a>
				</div>
			</div>
			{% else %}
			<div class="row cart-table" role="alert">
				<div class="col-sm-12 cart-td">
					<div class="product-label">
						Your cart is empty!
					</div>
				</div>
			</div>
			{% endif %}
		</div>
	</div>
</section>
{% endblock %}

{% block page_javascript %}
<script>
	window.cj = window.cj || {};
	window.cj.cart_id = {{ cart.id }};
</script>
<script src="{{ 'js/cart.js' | asset_url }}"></script>
{% endblock %}

var onQtySelect = function(e) {
	var $select = jQuery(this);
	var newQty = $select.val();
	var cartProduct = $select.data('cart-product');

	var xhr = jQuery.ajax('/cart/update_quantity/' + window.cj.cart_id + '/' + cartProduct, {
		data: JSON.stringify({
			'quantity': newQty
		}),
		contentType: 'application/json',
		dataType: 'json',
		type: 'POST'
	});

	xhr.done(function(data) {
		var $cp = jQuery('#cart-product-' + cartProduct);

		// Update product total
		var $price = $cp.find('.cart-product-price');
		var product = data.product;
		var price = (product.price / 100).toFixed(2);

		$price.text('$'  + price + ' USD');

		// Update cart subtotal
		var cartSubTotal = (data.subtotal / 100).toFixed(2);
		jQuery('#cart-subtotal-price').text('$' + cartSubTotal + ' USD');
	});
};

var onProdRem = function(e) {
	var $btn = jQuery(this);
	var cartProduct = $btn.data('cart-product');

	jQuery('#nav-cart-icon').removeClass('cart-add-bounce');
	var xhr = jQuery.ajax('/cart/rem/' + window.cj.cart_id + '/' + cartProduct, {
		contentType: 'application/json',
		dataType: 'json',
		type: 'POST'
	});
	$btn.attr('disabled', true);

	// Remove row on success
	xhr.done(function(data) {
		var $cp = jQuery("#cart-product-" + cartProduct);
		$cp.remove();

		if(jQuery('.row.cart-table').length == 1) {
			location.reload();
		}

		var rawPrice = data.subtotal;
		var price = (rawPrice / 100).toFixed(2);

		jQuery('#cart-subtotal-price').text('$' + price + ' USD');
		jQuery('#nav-cart-icon').text(data.cart_size).addClass('cart-add-bounce');
	});

	xhr.always(function() {
		$btn.attr('disabled', false);
	});
}

// Bind handlers
$(function() {
	jQuery('.product-qty-select').on('change', onQtySelect);
	jQuery('.product-rem-btn').on('click', onProdRem);
});

